    console.log("Immediately after setState:", this.state.count); // This will log the *old* count

    // To access the updated state after re-render, use the callback:
    this.setState({ count: this.state.count + 1 }, () => {
      console.log("In callback:", this.state.count); // This will log the updated count
    });

