  const [data, setData] = useState(); // Initially undefined

  useEffect(() => {
    fetch('https://api.example.com/data')
      .then(res => res.json())
      .then(data => setData(data)); 
  }, []);

  return (
    <div>
      {data.map(item => (  // Error occurs here when data is undefined
        <li key={item.id}>{item.name}</li>
      ))}
    </div>
  );
